import React, {useState} from 'react';
import './App.css';
import {ITodo} from './blueprints/todo'
import TodosCmp from './components/Todos'
import NewTodo from './components/NewTodo'

function App() {
  // const [todosList, setTodosList] = useState<{}>([{id: '', text: ''}, {id: '', text:''}])

  const [todo, setTodo] = useState<ITodo[]>([])

  const addHandler = (todoText: string) =>  {
    const newTodo: ITodo["text"] = todoText ;

    setTodo((prevTodo) => {
      return [...prevTodo, newTodo]
    })
  }

  return (
    <div>
      <h1>DREAM TEAM :</h1>
      <TodosCmp items={todo} />
      <NewTodo onAddTodo={addHandler} />
    </div>
  );
}

export default App;
